-- Vue pour afficher la hiérarchie complète
CREATE OR REPLACE VIEW `vue_hierarchie_complete` AS
SELECT 
    p.id,
    p.numero_compte,
    p.code_compte,
    p.libelle_compte,
    p.niveau,
    p.parent_id,
    p.classe,
    parent.libelle_compte AS parent_libelle,
    CASE p.niveau
        WHEN 0 THEN 'Classe'
        WHEN 1 THEN 'Sous-classe'
        WHEN 2 THEN 'Compte'
        WHEN 3 THEN 'Sous-compte'
        ELSE 'Détail'
    END AS type_compte
FROM plan_comptable p
LEFT JOIN plan_comptable parent ON p.parent_id = parent.numero_compte
ORDER BY p.numero_compte;

-- Vue pour les classes principales
CREATE OR REPLACE VIEW `vue_classes` AS
SELECT * FROM plan_comptable WHERE niveau = 0 ORDER BY numero_compte;

-- Vue pour les sous-classes
CREATE OR REPLACE VIEW `vue_sous_classes` AS
SELECT * FROM plan_comptable WHERE niveau = 1 ORDER BY numero_compte;

-- Vue pour les comptes principaux
CREATE OR REPLACE VIEW `vue_comptes` AS
SELECT * FROM plan_comptable WHERE niveau = 2 ORDER BY numero_compte;

-- Vue pour les sous-comptes
CREATE OR REPLACE VIEW `vue_sous_comptes` AS
SELECT * FROM plan_comptable WHERE niveau = 3 ORDER BY numero_compte;

-- Vue pour les comptes par classe
CREATE OR REPLACE VIEW `vue_comptes_par_classe` AS
SELECT 
    classe,
    COUNT(*) as total_comptes,
    SUM(CASE WHEN niveau = 0 THEN 1 ELSE 0 END) as classes,
    SUM(CASE WHEN niveau = 1 THEN 1 ELSE 0 END) as sous_classes,
    SUM(CASE WHEN niveau = 2 THEN 1 ELSE 0 END) as comptes,
    SUM(CASE WHEN niveau = 3 THEN 1 ELSE 0 END) as sous_comptes
FROM plan_comptable 
GROUP BY classe 
ORDER BY classe;

-- Fonction pour obtenir le chemin hiérarchique complet
DELIMITER //
CREATE FUNCTION GetCheminHierarchique(compte_numero VARCHAR(10))
RETURNS TEXT
READS SQL DATA
DETERMINISTIC
BEGIN
    DECLARE chemin TEXT DEFAULT '';
    DECLARE current_numero VARCHAR(10) DEFAULT compte_numero;
    DECLARE current_libelle VARCHAR(255);
    DECLARE current_parent VARCHAR(10);
    DECLARE niveau_count INT DEFAULT 0;
    
    WHILE current_numero IS NOT NULL AND niveau_count < 10 DO
        SELECT libelle_compte, parent_id 
        INTO current_libelle, current_parent
        FROM plan_comptable 
        WHERE numero_compte = current_numero;
        
        IF current_libelle IS NOT NULL THEN
            IF chemin = '' THEN
                SET chemin = current_libelle;
            ELSE
                SET chemin = CONCAT(current_libelle, ' > ', chemin);
            END IF;
        END IF;
        
        SET current_numero = current_parent;
        SET niveau_count = niveau_count + 1;
    END WHILE;
    
    RETURN chemin;
END //
DELIMITER ;

-- Procédure pour afficher l'arbre hiérarchique d'une classe
DELIMITER //
CREATE PROCEDURE AfficherArbreClasse(IN classe_numero VARCHAR(1))
BEGIN
    SELECT 
        CONCAT(REPEAT('  ', niveau), libelle_compte) AS arbre_hierarchique,
        numero_compte,
        niveau,
        parent_id
    FROM plan_comptable 
    WHERE classe = classe_numero 
    ORDER BY numero_compte;
END //
DELIMITER ;

-- Procédure pour valider la structure hiérarchique
DELIMITER //
CREATE PROCEDURE ValiderStructureHierarchique()
BEGIN
    -- Vérifier les comptes orphelins
    SELECT 'COMPTES ORPHELINS' as type_verification, numero_compte, libelle_compte
    FROM plan_comptable p1
    WHERE parent_id IS NOT NULL 
    AND NOT EXISTS (
        SELECT 1 FROM plan_comptable p2 
        WHERE p2.numero_compte = p1.parent_id
    )
    
    UNION ALL
    
    -- Vérifier les niveaux incohérents
    SELECT 'NIVEAUX INCOHÉRENTS' as type_verification, p1.numero_compte, p1.libelle_compte
    FROM plan_comptable p1
    JOIN plan_comptable p2 ON p1.parent_id = p2.numero_compte
    WHERE p1.niveau != p2.niveau + 1;
END //
DELIMITER ;

